import React, { Component } from "react";
import { Grid, Button, ButtonGroup } from "@material-ui/core";
import { Link } from "react-router-dom";
import "./Cart.css";

class Cart extends Component {
  state = {
    count: 1,
    price: 36000
  };
  render() {
    return (
      <div className="cart-container">
        <span className="cart-title">Cart (10 Products)</span>
        <div className="item-product">
          <Grid className="infor-product-container" container direction="row">
            <Grid style={{ width: 900 }} container direction="row">
              <Link to="/details">
                <img
                  src={require("../images/detail-white-2.jpg")}
                  alt="Cart"
                  className="image-product-cart"
                />
              </Link>
              <div className="infor-container">
                <Grid container direction="row">
                  <span class="title-product-cart">
                    Giày Sneaker Nam Thời Trang - Pettino GV03 - Đen - 39
                  </span>
                  <br />
                  <span class="title-product-cart">Chỉ còn 5 sản phẩm</span>
                  <br />
                  <span class="title-product-cart">Cung cấp bởi Pettino</span>
                  <br />
                  <Button style={{ color: "blue", fontSize: 12 }}>
                    Delete
                  </Button>
                </Grid>
              </div>
              <div className="infor-container">
                <h4>{this.state.price} VND</h4>
              </div>
              <div className="infor-container">
                <ButtonGroup className="button-group">
                  <Button
                    onClick={() => {
                      if (this.state.count > 1) {
                        this.setState({
                          count: this.state.count - 1,
                          price: this.state.price - 36000
                        });
                      }
                    }}
                  >
                    {" "}
                    -{" "}
                  </Button>
                  <Button>{this.state.count}</Button>
                  <Button
                    onClick={() => {
                      if (this.state.count > 1) {
                        this.setState({
                          count: this.state.count - 1,
                          price: this.state.price - 36000
                        });
                      }
                    }}
                  >
                    {" "}
                    +{" "}
                  </Button>
                </ButtonGroup>
              </div>
            </Grid>
            <div className="cost-container-right">HHHHH</div>
          </Grid>
        </div>
      </div>
    );
  }
}

export default Cart;

