import React, { Component } from "react";
import { TextField, Container, Button, IconButton } from "@material-ui/core";
import "../containers/LoginOrRegister.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEyeSlash, faEye } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";

class Login extends Component {
  state = {
    isShowPassword: false,
    email: "",
    password: ""
  };
  height = 0;
  constructor(props) {
    super(props);
    this.height = window.document.height;
  }
  render() {
    return (
      <div className="container" style={{ height: this.height }}>
        <h3>LOGIN</h3>
        <Container>
          <TextField
            className="textFieldStyle"
            label="Email"
            margin="dense"
            variant="filled"
            value={this.state.email}
            onChange={event => this.setState({ email: event.target.value })}
          />
          <br />
          <TextField
            className="textFieldStyle"
            label="Passworld"
            margin="dense"
            variant="filled"
            value={this.state.password}
            onChange={event => this.setState({ password: event.target.value })}
            type={this.state.isShowPassword ? "email" : "password"}
            color="white"
            InputProps={{
              endAdornment: (
                <IconButton
                  onClick={() => {
                    this.setState({
                      isShowPassword: !this.state.isShowPassword
                    });
                  }}
                >
                  <FontAwesomeIcon
                    icon={this.state.isShowPassword ? faEye : faEyeSlash}
                  />
                </IconButton>
              )
            }}
          />
          <br />
          <Button
            className="buttonStyle"
            onClick={this.handleLogin.bind(this)}
            style={{
              width: 500,
              marginTop: 50,
              background: "green",
              color: "white"
            }}
          >
            LOGIN
          </Button>
        </Container>
      </div>
    );
  }

  handleLogin() {
    if (this.state.email !== "" && this.state.password !== "") {
      var user =
        "{" +
        '"email": ' +
        this.state.email +
        "," +
        '"password":' +
        this.state.password +
        "}";
      axios
        .post(`https://jsonplaceholder.typicode.com/users`, { user })
        .then(res => {
          console.log(res);
          console.log(res.data);
        });
    } else {
      alert("Enter your emai or passw")
    }
  }
}

export default Login;
