import React, { Component } from "react";
import {
  Grid,
  Card,
  GridList,
  GridListTile,
  GridListTileBar,
  IconButton
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCartPlus,
  faHeart,
  faCheck
} from "@fortawesome/free-solid-svg-icons";
import Product from "../components/Product";
import axios from "axios";
import "../containers/Men.css";

const searchUrl = "https://webttcn.herokuapp.com/v1/shoes?q=";

class SearchResult extends Component {
  state = {
    sortSelect: "popular",
    liked: false,
    addToCart: false,
    searchText: "",
    shoes: []
  };

  componentDidMount() {
    const { searchText } = this.props.location.state;
    this.setState({ searchText: searchText });
    this.setState(searchText);
    axios.get(searchUrl, searchText).then(res => {
      this.setState({ shoes: res.data });
      console.log(this.state.shoes);
    });
  }

  render() {
    return (
      <div style={{ minHeight: 550, marginLeft: 60, marginRight: 60 }}>
        <Card
          style={{
            padding: 20,
            marginTop: 30,
            fontSize: 20,
            fontWeight: "bold"
          }}
        >
          Kết quả tìm kiếm: "Text"
        </Card>
        <GridList
          cellHeight={300}
          spacing={20}
          cols={4}
          style={{ marginBottom: 10 }}
        >
          {this.state.shoes.map(shoe => (
            <GridListTile>
              <Product
                id={shoe._id}
                likeTitle="100"
                image={
                  "https://webttcn.herokuapp.com/uploads/1574585783033-shoe_1.png"
                }
                name={shoe.name}
                price={shoe.price + " VNĐ"}
                handleToCart={isInCart => {
                  this.handleAddToCart(isInCart, shoe);
                }}
              />
            </GridListTile>
          ))}
        </GridList>
      </div>
    );
  }
}

export default SearchResult;
