import React, { Component } from "react";
import "./Notification.css";
import { Link, Grid, Button } from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBell } from "@fortawesome/free-solid-svg-icons";

class Notification extends Component {
  notifications = [
    "Giảm ngay 300k khi mua ĐH trên 1,500 triệu. Số lượng cực kỳ có hạn",
    "Notification 2",
    "Notification 3"
  ];
  render() {
    return (
      <div className="notification-container">
        {this.notifications.map(notification => (
          <div>
            <Grid>
              <Button className="">
                <div style={{ textAlign: "left", height: 40, marginTop: 10 }}>
                  <Link>
                    <FontAwesomeIcon icon={faBell} s/>
                    <span className="textNotifi">{notification}</span>
                  </Link>
                </div>
              </Button>
            </Grid>
          </div>
        ))}
      </div>
    );
  }
}

export default Notification;
