import axios from "axios";

const loginUrl = "https://webttcn.herokuapp.com/v1/auth/";
const registerUrl = "https://webttcn.herokuapp.com/v1/users/";
const getByIdUrl = "https://webttcn.herokuapp.com/v1/users/";
const urlCategories = "https://webttcn.herokuapp.com/v1/categories";

export function getCategories() {
  axios.get(urlCategories).then(res => {
    var categories = [];
    res.data.map(shoe => categories.push(shoe.name));
    return categories;
  });
}
