import React, { Component } from "react";
import { Grid, Button, ButtonGroup, Divider } from "@material-ui/core";
import { Link } from "react-router-dom";
import "./Cart.css";

class Cart extends Component {
  state = {
    count: 1,
    price: 36000
  };
  render() {
    return (
      <div className="cart-container">
        <span className="cart-title">Cart (10 Products)</span>
        <div style={{ marginLeft: 20, marginRight: 20 }}>
          <Grid container spacing={2}>
            <Grid item xs={9}>

            </Grid>
            <Grid
              style={{ backgroundColor: "white", marginTop: 8, height: 300 }}
              item
              xs={3}
            >
              <Grid>
                <div
                  style={{
                    marginBottom: 20,
                    marginTop: 20
                  }}
                >
                  <div style={{ height: 50 }}>
                    <span className="cost-span">Tạm tính: </span>
                    <span>52.288.000đ</span>
                  </div>
                  <Divider />
                  <div className="" style={{ height: 50, marginTop: 20 }}>
                    <span className="cost-span">Thành tiền: </span>
                    <span>52.288.000đ</span>
                  </div>
                </div>
                <div style={{ backgroundColor: "white", marginTop: 20 }}>
                  <Button
                    className="button-order-style"
                    style={{
                      color: "white",
                      backgroundColor: "red",
                      fontWeight: "bold"
                    }}
                  >
                    <span>Proceed to Order</span>
                  </Button>
                </div>
              </Grid>
            </Grid>
          </Grid>
        </div>
      </div>
    );
  }
}

export default Cart;

