import React from "react";
import { Slide } from "react-slideshow-image";
import "./SliderShow.css";

const slideImages = [
  "https://images-na.ssl-images-amazon.com/images/I/7177FQIcHBL._UL1500_.jpg",
  "https://i.ytimg.com/vi/BHdIz-FeMQ8/maxresdefault.jpg",
  "https://cdn1.i-scmp.com/sites/default/files/styles/1200x800/public/images/methode/2018/05/09/86c3f88a-528c-11e8-a252-5c54534dd764_1280x720_124903.jpg?itok=7CNYmPbn",
  "https://image.skechers.com/img/productimages/xlarge/51391_GYRD.jpg",
  "https://cdn11.bigcommerce.com/s-kmtd1qq/images/stencil/1280x1280/products/1089/4664/2-Cool-Casual-Navy__32798.1541163582.jpg?c=2&imbypass=on"
];

const properties = {
  duration: 5000,
  transitionDuration: 500,
  infinite: true,
  indicators: true,
  arrows: true,
  onChange: (oldIndex, newIndex) => {
    console.log(`slide transition from ${oldIndex} to ${newIndex}`);
  }
};

const SliderShow = () => {
  return (
    <div className="slide-container">
          <Slide {...properties}>

        <div
          className="each-slide"
          style={{ backgroundImage: `url(${slideImages[0]})` }}
        ></div>
        <div
          className="each-slide"
          style={{ backgroundImage: `url(${slideImages[1]})` }}
        ></div>
        <div
          className="each-slide"
          style={{ backgroundImage: `url(${slideImages[2]})` }}
        ></div>
        <div
          className="each-slide"
          style={{ backgroundImage: `url(${slideImages[3]})` }}
        ></div>
        <div
          className="each-slide"
          style={{ backgroundImage: `url(${slideImages[4]})` }}
        ></div>
      </Slide>
    </div>
  );
};

export default SliderShow;
