import React, { Component } from "react";
import { Grid, Button, ButtonGroup } from "@material-ui/core";
import { Link } from "react-router-dom";
import "../containers/Cart.css";

class CartProduct extends Component {
  render() {
    return (
      <Grid
        className="infor-product-container"
        container
        direction="row"
        justify="space-between"
        alignItems="center"
        style={{
          borderRadius: 8
        }}
      >
        <Link to={{ pathname: "/details", state: { id: "12345" } }}>
          <img
            src={require("../images/detail-white-2.jpg")}
            alt="Cart"
            className="image-product-cart"
          />
        </Link>
        <div className="infor-container">
          <Grid>
            <span class="title-product-cart">
              Giày Sneaker Nam Thời Trang - Pettino GV03 - Đen - 39
            </span>
            <br />
            <span class="title-product-cart">Chỉ còn 5 sản phẩm</span>
            <br />
            <span class="title-product-cart">Cung cấp bởi Pettino</span>
            <br />
            <Button
              style={{ color: "blue", fontSize: 12 }}
              onClick={this.handleDelete.bind(this)}
            >
              Delete
            </Button>
          </Grid>
        </div>
        <div className="infor-container">
          <h4>{"this.state.priceString"} VND</h4>
        </div>
        <div className="infor-container">
          <ButtonGroup className="button-group">
            <Button onClick={this.handleSub}> - </Button>
            <span className="number-product">{"this.state.count"}</span>
            <Button
              onClick={() => {
                // if (this.state.count < 100) {
                //   this.setState({
                //     count: this.state.count + 1,
                //     priceAllProducts: this.state.priceAllProducts + 36000,
                //     priceAllProductsString: this.priceToString(
                //       this.state.priceAllProducts + 36000
                //     )
                //   });
                // }
              }}
            >
              {" "}
              +{" "}
            </Button>
          </ButtonGroup>
        </div>
      </Grid>
    );
  }
  handleSub() {
    this.props.handleSub();
  }
  handleDelete() {}
}

export default CartProduct;
