import React, { Component } from "react";
import {
  Divider,
  Grid,
  CardMedia,
  Button,
  Select,
  MenuItem
} from "@material-ui/core";
import "./Details.css";

class DetailsProduct extends Component {
  state = {
    imageSelect: "detail-black.jpg",
    color: "black",
    size: 40
  };
  render() {
    return (
      <div>
        <Grid
          style={{ justifyContent: "space-between" }}
          container
          direction="row"
        >
          <div class="image-product-display">
            <span className="product-name">Birkenstock Milano</span>
            <CardMedia
              className="image-product"
              image={require("../images/" + this.state.imageSelect)}
            />
            <Grid container direction="row">
              <Button
                onClick={() => {
                  this.setState({ imageSelect: "detail-black.jpg" });
                }}
              >
                <CardMedia
                  className="image-button"
                  image={require("../images/detail-black.jpg")}
                />
              </Button>
              <Button
                onClick={() => {
                  this.setState({ imageSelect: "detail-white.jpg" });
                }}
              >
                <CardMedia
                  className="image-button"
                  image={require("../images/detail-white.jpg")}
                />
              </Button>
              <Button
                onClick={() => {
                  this.setState({ imageSelect: "detail-white-2.jpg" });
                }}
              >
                <CardMedia
                  className="image-button"
                  image={require("../images/detail-white-2.jpg")}
                />
              </Button>
            </Grid>
          </div>
          <div className="form-action">
            <span>$49.99</span>
            <br />
            <span>Colors:</span>
            <br />
            <Select
              style={{ width: 200 }}
              value={this.state.color}
              onChange={this.handleChangeColor.bind(this)}
            >
              <MenuItem value={"black"}>Black</MenuItem>
              <MenuItem value={"white"}>White</MenuItem>
              <MenuItem value={"yellow"}>Yellow</MenuItem>
            </Select>
            <br />
            <span>Size:</span>
            <br />
            <Select
              style={{ width: 200 }}
              value={this.state.color}
              onChange={this.handleChangeColor.bind(this)}
            >
              <MenuItem value={"black"}>Black</MenuItem>
              <MenuItem value={"white"}>White</MenuItem>
              <MenuItem value={"yellow"}>Yellow</MenuItem>
            </Select>
            <br />
            <Button color={"white"} bac className="button-add">
              Add to Shopping Bag
            </Button>
          </div>
        </Grid>
        <Divider style={{ height: 1, color: "black" }} />
        <h2>Product Information:</h2>
      </div>
    );
  }
  handleChangeColor(event) {
    this.setState({ color: event.target.value });
  }
  handleChangeSize() {}
}

export default DetailsProduct;
