import React, { Component } from "react";
import {
  Grid,
  Button,
  ButtonGroup,
  Divider,
  TextField
} from "@material-ui/core";
import { Link } from "react-router-dom";
import "./Cart.css";

class Cart extends Component {
  state = {
    count: 1,
    price: 36000
  };
  render() {
    return (
      <div className="cart-container">
        <span className="cart-title">Giỏ hàng (10 Sản phẩm):</span>
        <div style={{ marginLeft: 30, marginRight: 30 }}>
          <Grid container spacing={2}>
            <Grid item xs={9}>
              {[1, 2, 3, 4].map(() => (
                <Grid
                  className="infor-product-container"
                  container
                  direction="row"
                  justify="space-between"
                  alignItems="center"
                  style={{
                    borderRadius: 8
                  }}
                >
                  <Link to="/details">
                    <img
                      src={require("../images/detail-white-2.jpg")}
                      alt="Cart"
                      className="image-product-cart"
                    />
                  </Link>
                  <div className="infor-container">
                    <Grid>
                      <span class="title-product-cart">
                        Giày Sneaker Nam Thời Trang - Pettino GV03 - Đen - 39
                      </span>
                      <br />
                      <span class="title-product-cart">Chỉ còn 5 sản phẩm</span>
                      <br />
                      <span class="title-product-cart">
                        Cung cấp bởi Pettino
                      </span>
                      <br />
                      <Button style={{ color: "blue", fontSize: 12 }}>
                        Delete
                      </Button>
                    </Grid>
                  </div>
                  <div className="infor-container">
                    <h4>{this.state.price} VND</h4>
                  </div>
                  <div className="infor-container">
                    <ButtonGroup className="button-group">
                      <Button
                        onClick={() => {
                          if (this.state.count > 1) {
                            this.setState({
                              count: this.state.count - 1,
                              price: this.state.price - 36000
                            });
                          }
                        }}
                      >
                        {" "}
                        -{" "}
                      </Button>
                      <TextField className="number-product" onChange={(event) => {
                        this.setState
                      }} />
                      <Button
                        onClick={() => {
                          if (this.state.count > 1) {
                            this.setState({
                              count: this.state.count - 1,
                              price: this.state.price - 36000
                            });
                          }
                        }}
                      >
                        {" "}
                        +{" "}
                      </Button>
                    </ButtonGroup>
                  </div>
                </Grid>
              ))}
            </Grid>
            <Grid
              style={{
                backgroundColor: "white",
                marginTop: 8,
                height: 300,
                borderRadius: 8
              }}
              item
              xs={3}
            >
              <Grid>
                <div
                  style={{
                    marginBottom: 20,
                    marginTop: 20
                  }}
                >
                  <div style={{ height: 50 }}>
                    <span className="cost-span">Tạm tính: </span>
                    <span>52.288.000đ</span>
                  </div>
                  <Divider />
                  <div className="" style={{ height: 50, marginTop: 20 }}>
                    <span className="cost-span">Thành tiền: </span>
                    <span>52.288.000đ</span>
                  </div>
                </div>
                <div style={{ backgroundColor: "white", marginTop: 20 }}>
                  <Button
                    className="button-order-style"
                    style={{
                      color: "white",
                      backgroundColor: "red",
                      fontWeight: "bold",
                      textTransform: "none",
                      fontSize: 17
                    }}
                  >
                    Thanh toán
                  </Button>
                </div>
              </Grid>
            </Grid>
          </Grid>
        </div>
      </div>
    );
  }
}

export default Cart;
