import React, { Component } from "react";
import {
  Grid,
  Card,
  Radio,
  FormControlLabel,
  RadioGroup
} from "@material-ui/core";
import InputRangeCustom from "../components/InputRangeCustom";
import axios from "axios";

const categoriesUrl = "https://webttcn.herokuapp.com/v1/categories";

export default class FillterSelect extends Component {
  constructor(props) {
    super(props);
    this.state = {
      categorySelected: "",
      categories: [],
      priceValue: 0
    };
  }

  componentDidMount() {
    axios
      .get(categoriesUrl)
      .then(res => {
        this.setState({
          categories: res.data,
          categorySelected: res.data[0].name
        });
      })
      .catch(error => console.log(error));
  }
  render() {
    return (
      <Grid item xs={2}>
        <p style={{ fontWeight: "bold", fontSize: 25 }}>Giày nữ</p>
        <Card style={{ padding: 10 }}>Bộ lọc kết quả</Card>
        <Card style={{ marginTop: 30 }}>
          <Card style={{ padding: 10 }}>Theo thương hiệu</Card>
          <Grid container className="center-select">
            <RadioGroup
              value={this.state.categorySelected}
              onChange={this.handleChangeCategory.bind(this)}
            >
              {this.state.categories.map(categori => (
                <div>
                  <Grid>
                    <FormControlLabel
                      style={{ marginLeft: 20 }}
                      control={<Radio value={categori.name} />}
                      label={categori.name}
                    />
                  </Grid>
                  <br />
                </div>
              ))}
            </RadioGroup>
          </Grid>
        </Card>
        <Card style={{ marginTop: 30, padding: 30 }}>
          <Card style={{ padding: 10, marginBottom: 30 }}>
            Theo giá (Ngàn đồng)
          </Card>
          <InputRangeCustom onChangePrice={price => console.log(price)} />
        </Card>
      </Grid>
    );
  }
  handleChangeCategory(event) {
    this.setState({ categorySelected: event.target.value });
  }
}
