import React, { Component } from "react";
import { Grid, IconButton, CardMedia, Avatar } from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChevronLeft,
  faChevronRight
} from "@fortawesome/free-solid-svg-icons";

class LandingPage extends Component {
  listImageUrls = [
    "https://images-na.ssl-images-amazon.com/images/I/7177FQIcHBL._UL1500_.jpg",
    "https://i.ytimg.com/vi/BHdIz-FeMQ8/maxresdefault.jpg",
    "https://cdn1.i-scmp.com/sites/default/files/styles/1200x800/public/images/methode/2018/05/09/86c3f88a-528c-11e8-a252-5c54534dd764_1280x720_124903.jpg?itok=7CNYmPbn",
    "https://image.skechers.com/img/productimages/xlarge/51391_GYRD.jpg",
    "https://cdn11.bigcommerce.com/s-kmtd1qq/images/stencil/1280x1280/products/1089/4664/2-Cool-Casual-Navy__32798.1541163582.jpg?c=2&imbypass=on"
  ];

  state = {
    index: 0,
    colorsPageControl: ["black", "gray", "gray", "gray", "gray"]
  };

  constructor(props) {
    super(props);
    setInterval(() => {
      this.setState({ index: this.state.index + 1 });
      if (this.state.index < this.listImageUrls.length - 1) {
        this.setState({
          index: this.state.index + 1
        });
        var colors = ["gray", "gray", "gray", "gray", "gray"];
        colors[this.state.index + 1] = "black";
        this.setState({ colorsPageControl: colors });
      } else {

      }
    }, 1000);
  }
  render() {
    return (
      <div>
        <CardMedia
          image={this.listImageUrls[this.state.index]}
          width="100%"
          style={{
            height: "700px",
            justifyContent: "center",
            alignItems: "center"
          }}
        >
          <Grid
            container
            direction="row"
            style={{
              alignContent: "center",
              alignItems: "center",
              justifyContent: "space-between",
              flexGrow: 1,
              height: 670
            }}
          >
            <IconButton
              onClick={this.handleBackClick.bind(this)}
              className="iconButtonStyle"
            >
              <FontAwesomeIcon icon={faChevronLeft} />
            </IconButton>
            <IconButton
              onClick={this.handleNextClick.bind(this)}
              className="iconButtonStyle"
            >
              <FontAwesomeIcon icon={faChevronRight} />
            </IconButton>
          </Grid>
          <Grid
            container
            direction="row"
            style={{
              alignItems: "center",
              justifyContent: "center"
            }}
          >
            {this.state.colorsPageControl.map(color => (
              <div className="pageControl">
                <Avatar
                  style={{
                    width: 10,
                    height: 10,
                    backgroundColor: color,
                    margin: 2
                  }}
                />
              </div>
            ))}
          </Grid>
        </CardMedia>
      </div>
    );
  }

  handleBackClick() {
    if (this.state.index > 0) {
      this.setState({
        index: this.state.index - 1
      });
      var colors = ["gray", "gray", "gray", "gray", "gray"];
      colors[this.state.index - 1] = "black";
      this.setState({ colorsPageControl: colors });
    }
  }

  handleNextClick() {
    if (this.state.index < this.listImageUrls.length - 1) {
      this.setState({
        index: this.state.index + 1
      });
      var colors = ["gray", "gray", "gray", "gray", "gray"];
      colors[this.state.index + 1] = "black";
      this.setState({ colorsPageControl: colors });
    }
  }

  setupPageControl() {}
}

export default LandingPage;
