import React, { Component } from "react";
import {
  Grid,
  Card,
  GridListTile,
  GridListTileBar,
  IconButton
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCartPlus,
  faHeart,
  faCheck
} from "@fortawesome/free-solid-svg-icons";

import "../containers/Men.css";
class Product extends Component {
  state = {
    sortSelect: "popular",
    liked: false,
    addToCart: false
  };
  render() {
    return (
      <div>
        <GridListTile>
          <Card style={{ marginTop: 20 }} className="border-product">
            <Grid container>
              <IconButton
                onClick={() => this.setState({ liked: !this.state.liked })}
                style={{ color: "gray" }}
              >
                <FontAwesomeIcon
                  color={this.state.liked ? "gray" : "blue"}
                  icon={faHeart}
                />
              </IconButton>
              <p style={{ fontSize: 17 }}>{this.props.title}</p>
            </Grid>
            <img
              src={require("../images/detail-white-2.jpg")}
              alt="shoe"
              style={{ height: 250 }}
            />
            <GridListTileBar
              title={"Name"}
              subtitle={"Cost"}
              actionIcon={
                <IconButton
                  className="icon-size"
                  onClick={() =>
                    this.setState({ addToCart: !this.state.addToCart })
                  }
                  style={{ color: "white" }}
                >
                  <FontAwesomeIcon
                    icon={this.state.addToCart ? faCheck : faCartPlus}
                  />
                </IconButton>
              }
            />
          </Card>
        </GridListTile>
      </div>
    );
  }
}

export default Product;
