import React, { Component } from "react";
import { Grid, IconButton, CardMedia } from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faChevronLeft,
  faChevronRight
} from "@fortawesome/free-solid-svg-icons";

class LandingPage extends Component {
  listImageUrls = [
    "https://images-na.ssl-images-amazon.com/images/I/7177FQIcHBL._UL1500_.jpg",
    "https://i.ytimg.com/vi/BHdIz-FeMQ8/maxresdefault.jpg",
    "https://cdn1.i-scmp.com/sites/default/files/styles/1200x800/public/images/methode/2018/05/09/86c3f88a-528c-11e8-a252-5c54534dd764_1280x720_124903.jpg?itok=7CNYmPbn",
    "https://image.skechers.com/img/productimages/xlarge/51391_GYRD.jpg",
    "https://cdn11.bigcommerce.com/s-kmtd1qq/images/stencil/1280x1280/products/1089/4664/2-Cool-Casual-Navy__32798.1541163582.jpg?c=2&imbypass=on"
  ];
  state = {
    i: 0
  };
  render() {
    return (
      <div>
        <CardMedia
          image={this.state.urlImage}
          width="100%"
          style={{
            height: "700px"
          }}
        >
          <Grid
            container
            direction="row"
            style={{
              alignContent: "center",
              alignItems: "center",
              justifyContent: "space-between",
              flexGrow: 1
            }}
          >
            <IconButton onClick={this.handleBackClick}>
              <FontAwesomeIcon style={{ color: "grey" }} icon={faChevronLeft} />
            </IconButton>
            <IconButton onClick={this.handleNextClick}>
              <FontAwesomeIcon
                style={{ color: "grey" }}
                icon={faChevronRight}
              />
            </IconButton>
          </Grid>
        </CardMedia>
      </div>
    );
  }
  handleBackClick() {
    this.setState({
      urlImage: "https://i.ytimg.com/vi/BHdIz-FeMQ8/maxresdefault.jpg"
    });
  }

  handleNextClick() {
    this.setState({
      urlImage:
        "https://image.skechers.com/img/productimages/xlarge/51391_GYRD.jpg"
    });
  }
}

export default LandingPage;
