import React, { Component } from "react";
import {
  Button,
  AppBar,
  Toolbar,
  Input,
  CardMedia,
  IconButton,
  Grid,
  Menu,
  MenuItem,
  Fade,
  InputAdornment
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faShoppingBag,
  faUserAlt,
  faBell,
  faSearch,
  faSignInAlt,
  faSignOutAlt
} from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import "./Header.css";

class Header extends Component {
  state = {
    searchText: "",
    isOpen: false,
    anchorEl: null
  };

  handleClick() {
    this.setState({ isOpen: !this.state.isOpen });
  }
  handleClose() {
    this.setState({ isOpen: false });
  }

  ButtonBarStyle = {
    color: "white",
    disableUnderline: "true",
    textDecoration: "none"
  };

  MenuButtonStyle = {
    color: "black",
    disableUnderline: "true",
    textDecoration: "none"
  };

  render() {
    return (
      <AppBar position="static" color="primary">
        <Toolbar
          style={{
            alignContent: "center",
            alignItems: "center",
            justifyContent: "space-between"
          }}
        >
          <Toolbar>
            <Link to="/home">
              <CardMedia
                style={{ width: 70, height: 30, borderRadius: 10 }}
                image={require("../images/bit-log.png")}
              />
            </Link>
            <Link to="/home" style={this.ButtonBarStyle}>
              <Button
                className="header-button-style"
                style={{
                  color: "white",
                  fontWeight: "bold",
                  textTransform: "none"
                }}
                onClick={() => {}}
              >
                Trang chủ
              </Button>
            </Link>
            <Link to="/men" style={this.ButtonBarStyle}>
              <Button
                className="header-button-style"
              >
                Giày nam
              </Button>
            </Link>

            <Link to="/women" style={this.ButtonBarStyle}>
              <Button
                style={{
                  color: "white",
                  fontWeight: "bold"
                }}
              >
                Giày nữ
              </Button>
            </Link>
          </Toolbar>
          <Input
            value={this.state.searchText}
            onChange={event =>
              this.setState({ searchText: event.target.value })
            }
            placeholder="Tìm sản phẩm mong muốn ..."
            disableUnderline={true}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  onClick={() => {
                    console.log(this.state.searchText);
                  }}
                >
                  <FontAwesomeIcon icon={faSearch} />
                </IconButton>
              </InputAdornment>
            }
            style={{
              height: 40,
              width: 500,
              color: "gray",
              background: "white",
              borderColor: "black",
              borderWidth: 2,
              borderRadius: 5,
              padding: "0 10px",
              border: 2,
              boxShadow: props =>
                props.color === "red"
                  ? "0 3px 5px 2px rgba(255, 105, 135, .3)"
                  : "0 3px 5px 2px rgba(33, 203, 243, .3)"
            }}
          />
          <Grid>
            <IconButton
              aria-haspopup="true"
              onClick={event => {
                this.setState({
                  isOpen: true,
                  anchorEl: event.currentTarget
                });
              }}
            >
              <FontAwesomeIcon style={{ color: "white" }} icon={faUserAlt} />
            </IconButton>
            <Menu
              anchorEl={this.state.anchorEl}
              open={this.state.isOpen}
              TransitionComponent={Fade}
              keepMounted
            >
              <MenuItem
                onClick={() => {
                  this.setState({
                    isOpen: false,
                    anchorEl: null
                  });
                }}
              >
                <Link to="/loginorregister" style={this.MenuButtonStyle}>
                  <FontAwesomeIcon icon={faSignInAlt} />
                  <span> Login or Register</span>
                </Link>
              </MenuItem>
              <MenuItem
                onClick={() => {
                  this.setState({ isOpen: false, anchorEl: null });
                }}
              >
                <Link to="/myaccount" style={this.MenuButtonStyle}>
                  <FontAwesomeIcon icon={faUserAlt} />
                  <span> My Account</span>
                </Link>
              </MenuItem>
              <MenuItem
                onClick={() => {
                  this.setState({ isOpen: false, anchorEl: null });
                }}
              >
                <FontAwesomeIcon icon={faSignOutAlt} />
                <span> Sign Out</span>
              </MenuItem>
            </Menu>

            <Link to="/notification">
              <IconButton>
                <FontAwesomeIcon style={{ color: "white" }} icon={faBell} />
              </IconButton>
            </Link>

            <Link to="/cart">
              <IconButton>
                <FontAwesomeIcon
                  style={{ color: "white" }}
                  icon={faShoppingBag}
                />
              </IconButton>
            </Link>
          </Grid>
        </Toolbar>
      </AppBar>
    );
  }
}

export default Header;
