import React, { Component } from "react";
import {
  Button,
  AppBar,
  Toolbar,
  Input,
  CardMedia,
  IconButton,
  Grid,
  Menu,
  MenuItem,
  Fade
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faShoppingBag,
  faUserAlt,
  faBell
} from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { BrowserRouter, Route } from "react-router-dom";
import Register from "./user_interface/components/Register";
import Login from "./user_interface/components/Login";
import Home from "./user_interface/components/Home";
import Men from "./user_interface/components/Home";
import Women from "./user_interface/components/Home";

class Header extends Component {
  state = {
    textInput: "",
    isOpen: false
  };

  handleClick() {
    this.setState({ isOpen: !this.state.isOpen });
  }
  handleClose() {
    this.setState({ isOpen: false });
  }

  ButtonBarStyle = {
    color: "white"
  };

  render() {
    return (
      <div>
        <AppBar position="static" color="primary">
          <Toolbar
            style={{
              alignContent: "center",
              alignItems: "center",
              justifyContent: "space-between"
            }}
          >
            <Toolbar>
              <CardMedia
                style={{ width: 70, height: 30, borderRadius: 10 }}
                image="https://storage.googleapis.com/bit-docs/bit-logo%402x.png"
              />
              <Button onClick={() => {}}>
                <Link
                  disableUnderline={true}
                  to="/home"
                  style={this.ButtonBarStyle}
                >
                  Home
                </Link>
              </Button>
              <Button>
                <Link
                  disableUnderline={true}
                  to="/men"
                  style={this.ButtonBarStyle}
                >
                  Men
                </Link>
              </Button>
              <Button>
                <Link
                  disableUnderline={true}
                  to="/women"
                  style={this.ButtonBarStyle}
                >
                  Women
                </Link>
              </Button>
            </Toolbar>
            <Input
              onChange={this.inputHandle}
              placeholder="Search..."
              disableUnderline={true}
              style={{
                height: 40,
                width: 500,
                color: "gray",
                background: "white",
                paddingRight: 5,
                paddingLeft: 5,
                borderColor: "black",
                borderWidth: 2,
                borderRadius: 15,
                padding: "0 10px",
                border: 2,
                boxShadow: props =>
                  props.color === "red"
                    ? "0 3px 5px 2px rgba(255, 105, 135, .3)"
                    : "0 3px 5px 2px rgba(33, 203, 243, .3)"
              }}
            />
            <Grid>
              <IconButton>
                <FontAwesomeIcon
                  style={{ color: "white" }}
                  icon={faUserAlt}
                  onClick={() => {
                    this.setState({ isOpen: true });
                  }}
                />
              </IconButton>
              <Menu
                open={this.state.isOpen}
                TransitionComponent={Fade}
                keepMounted
              >
                <MenuItem
                  onClick={() => {
                    this.setState({ isOpen: false });
                  }}
                >
                  Login or Register
                </MenuItem>
                <MenuItem
                  onClick={() => {
                    this.setState({ isOpen: false });
                  }}
                >
                  My account
                </MenuItem>
                <MenuItem
                  onClick={() => {
                    this.setState({ isOpen: false });
                  }}
                >
                  Logout
                </MenuItem>
              </Menu>
              <IconButton>
                <FontAwesomeIcon style={{ color: "white" }} icon={faBell} />
              </IconButton>
              <IconButton>
                <FontAwesomeIcon
                  style={{ color: "white" }}
                  icon={faShoppingBag}
                />
              </IconButton>
            </Grid>
          </Toolbar>
        </AppBar>
        <div className="main-route-place">
          <Route exact path="/home" component={Home} />
          <Route path="/men" component={Men} />
          <Route path="/women" component={Women} />
          <Route path="/login" component={Login} />
          <Route path="/register" component={Register} />
        </div>
      </div>
    );
  }
}

export default Header;
