import React, { Component } from "react";
import { Grid, Button, ButtonGroup, Divider } from "@material-ui/core";
import { Link } from "react-router-dom";
import "./Cart.css";

class Cart extends Component {
  state = {
    count: 1,
    price: 36000
  };
  render() {
    return (
      <div className="cart-container">
        <span className="cart-title">Cart (10 Products)</span>
        <div style={{ marginLeft: 20, marginRight: 20 }}>
          <Grid container spacing={2}>
            <Grid style={{ marginLeft: 0 }} item xs={9}>
              <Grid
                className="infor-product-container"
                container
                direction="row"
              >
                <Link to="/details">
                  <img
                    src={require("../images/detail-white-2.jpg")}
                    alt="Cart"
                    className="image-product-cart"
                  />
                </Link>
                <div className="infor-container">
                  <Grid>
                    <span class="title-product-cart">
                      Giày Sneaker Nam Thời Trang - Pettino GV03 - Đen - 39
                    </span>
                    <br />
                    <span class="title-product-cart">Chỉ còn 5 sản phẩm</span>
                    <br />
                    <span class="title-product-cart">Cung cấp bởi Pettino</span>
                    <br />
                    <Button style={{ color: "blue", fontSize: 12 }}>
                      Delete
                    </Button>
                  </Grid>
                </div>
                <div className="infor-container">
                  <h4>{this.state.price} VND</h4>
                </div>
                <div className="infor-container">
                  <ButtonGroup className="button-group">
                    <Button
                      onClick={() => {
                        if (this.state.count > 1) {
                          this.setState({
                            count: this.state.count - 1,
                            price: this.state.price - 36000
                          });
                        }
                      }}
                    >
                      {" "}
                      -{" "}
                    </Button>
                    <Button>{this.state.count}</Button>
                    <Button
                      onClick={() => {
                        if (this.state.count > 1) {
                          this.setState({
                            count: this.state.count - 1,
                            price: this.state.price - 36000
                          });
                        }
                      }}
                    >
                      {" "}
                      +{" "}
                    </Button>
                  </ButtonGroup>
                </div>
              </Grid>
            </Grid>
            <Grid style={{ marginRight: 0 }} item xs={3}>
              <Grid>
                <div style={{ backgroundColor: "white", marginBottom: 20 }}>
                  <span>Tạm tính:52.288.000đ</span>
                </div>
                <Divider />
                <div style={{ backgroundColor: "white" }}>
                  <span>Thành tiền: 52.288.000đ</span>
                </div>
              </Grid>
            </Grid>
          </Grid>
        </div>
      </div>
    );
  }
}

export default Cart;

