import React, { Component } from "react";
import {
  GridList,
  Grid,
  Container,
  Button,
  Card,
  Paper,
  Typography,
  IconButton
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faMinus } from "@fortawesome/free-solid-svg-icons";
import "./Cart.css";

class Cart extends Component {
  state = {
    count: 0
  };
  render() {
    return (
      <Container>
        <h2>Cart</h2>
        <GridList style={{ marginRight: 100, marginLeft: 100 }} cols={1}>
          {["white", "yellow", "blue", "red"].map(color => (
            <Card className="cardStyle">
              <Grid container direction="row" className="gridContainer">
                <img
                  src="https://salt.tikicdn.com/cache/175x175/ts/product/9c/ad/e4/1efb61539a6adcf2da14bd70b21ddf58.jpg"
                  alt="Cart"
                />
                <Grid>
                  <Paper className="producInfor">
                    <Typography variant="h5" component="h3">
                      Cà phê Sôcôla
                    </Typography>
                    <Typography component="p">
                      Paper can be used to build surface or other elements for
                      your application.
                    </Typography>
                  </Paper>
                  <Button className="deleteButton" style={{ color: "blue" }}>
                    Delete
                  </Button>
                </Grid>
                <h4>36.000đ</h4>

                <Grid className="count" direction="column">
                  <IconButton onClick={() => {
                    this.setState({count: this.state.count + 1})
                  }}>
                    <FontAwesomeIcon icon={faMinus} className="iconStyle" />
                  </IconButton>
                  <h4>{this.state.count}</h4>
                  <IconButton>
                    <FontAwesomeIcon icon={faPlus} className="iconStyle" />
                  </IconButton>
                </Grid>
              </Grid>
            </Card>
          ))}
        </GridList>
      </Container>
    );
  }
}

export default Cart;

