import React, { Component } from "react";
import {
  AppBar,
  Toolbar,
  Input,
  CardMedia,
  IconButton,
  Grid,
  Menu,
  MenuItem,
  Fade,
  ButtonGroup,
  Button
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faShoppingBag,
  faUserAlt,
  faBell,
  faSearch,
  faSignInAlt,
  faSignOutAlt
} from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import HeaderButton from "./HeaderButton";

class Header extends Component {
  state = {
    searchText: "",
    isOpen: false,
    anchorEl: null
  };

  handleClick() {
    this.setState({ isOpen: !this.state.isOpen });
  }
  handleClose() {
    this.setState({ isOpen: false });
  }

  ButtonBarStyle = {
    color: "white",
    disableUnderline: "true",
    textDecoration: "none"
  };

  MenuButtonStyle = {
    color: "black",
    disableUnderline: "true",
    textDecoration: "none"
  };

  render() {
    return (
      <AppBar
        position="static"
        color="primary"
        style={{ backgroundColor: "#5199FF", height: 65 }}
      >
        <Toolbar
          style={{
            alignContent: "center",
            alignItems: "center",
            justifyContent: "space-between"
          }}
        >
          <Toolbar>
            <Link to="/home">
              <CardMedia
                style={{ width: 70, height: 30, borderRadius: 10 }}
                image={require("../images/bit-log.png")}
              />
            </Link>
            <Link to="/home" style={this.ButtonBarStyle}>
              <HeaderButton label="Trang chủ" />
            </Link>
            <Link to="/men" style={this.ButtonBarStyle}>
              <HeaderButton label="Giày nam" />
            </Link>
            <Link to="/women" style={this.ButtonBarStyle}>
              <HeaderButton label="Giày nữ" />
            </Link>
          </Toolbar>
          <Toolbar item xs={12} md={6}>
            <Input
              value={this.state.searchText}
              onChange={event =>
                this.setState({ searchText: event.target.value })
              }
              placeholder="Tìm sản phẩm mong muốn ..."
              disableUnderline={true}
              style={{
                height: 40,
                width: 500,
                color: "gray",
                borderBottomLeftRadius: 5,
                borderTopLeftRadius: 5,
                backgroundColor: "white",
                padding: "0 10px",
                fontSize: 14,
                boxShadow: props =>
                  props.color === "red"
                    ? "0 3px 5px 2px rgba(255, 105, 135, .3)"
                    : "0 3px 5px 2px rgba(33, 203, 243, .3)"
              }}
            />
            <Button
              style={{
                borderBottomLeftRadius: 0,
                borderTopLeftRadius: 0,
                borderTopRightRadius: 5,
                borderBottomRightRadius: 5,
                height: 40,
                width: 70,
                color: "gray",
                fontSize: 20,
                backgroundColor: "#F2F8FD"
              }}
              onClick={() => {}}
            >
              <FontAwesomeIcon icon={faSearch} />
            </Button>
          </Toolbar>
          <Grid>
            <IconButton
              aria-haspopup="true"
              onClick={event => {
                this.setState({
                  isOpen: true,
                  anchorEl: event.currentTarget
                });
              }}
            >
              <FontAwesomeIcon style={{ color: "white" }} icon={faUserAlt} />
            </IconButton>
            <Menu
              anchorEl={this.state.anchorEl}
              open={this.state.isOpen}
              TransitionComponent={Fade}
              keepMounted
            >
              <MenuItem
                onClick={() => {
                  this.setState({
                    isOpen: false,
                    anchorEl: null
                  });
                }}
              >
                <Link to="/loginorregister" style={this.MenuButtonStyle}>
                  <FontAwesomeIcon
                    style={{ marginRight: 5 }}
                    icon={faSignInAlt}
                  />
                  <span> Đăng nhập </span>
                </Link>
              </MenuItem>
              <MenuItem
                onClick={() => {
                  this.setState({ isOpen: false, anchorEl: null });
                }}
              >
                <Link to="/myaccount" style={this.MenuButtonStyle}>
                  <FontAwesomeIcon
                    style={{ marginRight: 5 }}
                    icon={faUserAlt}
                  />
                  <span> Tài khoản của bạn</span>
                </Link>
              </MenuItem>
              <MenuItem
                onClick={() => {
                  this.setState({ isOpen: false, anchorEl: null });
                }}
              >
                <FontAwesomeIcon
                  style={{ marginRight: 5 }}
                  icon={faSignOutAlt}
                />
                <span> Đăng xuất</span>
              </MenuItem>
            </Menu>

            <Link to="/notification">
              <IconButton>
                <FontAwesomeIcon style={{ color: "white" }} icon={faBell} />
              </IconButton>
            </Link>

            <Link to="/cart">
              <IconButton>
                <FontAwesomeIcon
                  style={{ color: "white" }}
                  icon={faShoppingBag}
                />
              </IconButton>
            </Link>
          </Grid>
        </Toolbar>
      </AppBar>
    );
  }
}

export default Header;
