import React, { Component } from "react";
import { Grid, Button, Divider } from "@material-ui/core";
import CartProduct from "../components/CartProduct";
import axios from "axios";
import "./Cart.css";

const getShoeByID = "https://webttcn.herokuapp.com/v1/shoes";
const billsUrl = "https://webttcn.herokuapp.com/v1/bills";

class Cart extends Component {
  state = {
    priceAllProducts: 0,
    shoes: [],
    listOfShoes: []
  };

  componentDidMount() {
    var idGets = JSON.parse(localStorage.getItem("ids"));
    if (idGets == null) {
      idGets = [];
    } else {
      idGets.map(id => {
        axios.get(`${getShoeByID}/${id}`).then(res => {
          this.setState({
            shoes: [...this.state.shoes, res.data],
            priceAllProducts: this.state.priceAllProducts + res.data.price,
            listOfShoes: [
              ...this.state.listOfShoes,
              {
                _id: res.data._id,
                amount: 1
              }
            ]
          });

          console.log(res.data);
        });
      });
    }
    console.log(this.state.shoes);
  }
  render() {
    return (
      <div className="cart-container">
        <span className="cart-title">Giỏ hàng (10 Sản phẩm):</span>
        <div style={{ marginLeft: 30, marginRight: 30 }}>
          <Grid container spacing={2}>
            <Grid item xs={9}>
              {this.state.shoes.map(shoe => (
                <CartProduct
                  handleSubRecieve={this.handleSub.bind(this)}
                  handlePlusReceive={this.handlePlus.bind(this)}
                  count={1}
                  name={shoe.name}
                  price={this.priceToString(shoe.price)}
                  category={shoe.category.name}
                  imageUrl={"https://webttcn.herokuapp.com/" + shoe.images[0]}
                />
              ))}
            </Grid>
            <Grid
              style={{
                backgroundColor: "white",
                marginTop: 8,
                height: 200,
                borderRadius: 8
              }}
              item
              xs={3}
            >
              <Grid>
                <div
                  style={{
                    marginBottom: 20,
                    marginTop: 20
                  }}
                >
                  <div style={{ height: 50 }}>
                    <span style={{ fontSize: 15, color: "black" }}>
                      Tạm tính:{" "}
                    </span>
                    <span style={{ fontSize: 15, color: "black" }}>
                      {this.priceToString(this.state.priceAllProducts)}đ
                    </span>
                  </div>
                  <Divider />
                  <div className="" style={{ height: 50, marginTop: 30 }}>
                    <span style={{ fontSize: 15, color: "black" }}>
                      Thành tiền:{" "}
                    </span>
                    <span style={{ fontSize: 18, color: "red" }}>
                      {this.priceToString(this.state.priceAllProducts)}đ
                    </span>
                    <br />
                    <span style={{ fontSize: 13 }}>
                      (Đã bao gồm VAT nếu có)
                    </span>
                  </div>
                </div>
                <div style={{ backgroundColor: "white", marginTop: 90 }}>
                  <Button
                    className="button-order-style"
                    style={{
                      color: "white",
                      backgroundColor: "red",
                      fontWeight: "bold",
                      textTransform: "none",
                      fontSize: 17
                    }}
                    onClick={this.handleBills.bind(this)}
                  >
                    Thanh toán
                  </Button>
                </div>
              </Grid>
            </Grid>
          </Grid>
        </div>
      </div>
    );
  }
  handleBills() {
    var bill = {
      userId: localStorage.get("id_user"),
      address: "Da Nang",
      money: this.state.priceAllProducts,
      listOfShoes: this.state.listOfShoes
    };
    axios
      .post(billsUrl, bill)
      .then(res => {
        console.log(res.data);
      })
      .catch(err => {
        alert("Thanh toán thất bại!");
      });
  }

  handlePlus() {
    console.log("aa");
  }

  handleSub() {
    console.log("aa");
  }

  onHandleClick(id) {
    this.props.onReceiveId(id);
  }

  priceToString(price) {
    return price.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1.");
  }

  handleDelete() {}
}

export default Cart;
