import React, { Component } from "react";
import "./Notification.css";
import { Link, Grid, Button, Divider, Paper } from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBell } from "@fortawesome/free-solid-svg-icons";

class Notification extends Component {
  notifications = [
    "Giảm ngay 300k khi mua ĐH trên 1,500 triệu. Số lượng cực kỳ có hạn",
    "Notification 2",
    "Notification 3"
  ];
  render() {
    return (
      <div className="notification-container">
        <Paper
          style={{
            height: 50,
            marginB
          }}
        >
          <p style={{}}>Thông báo của tôi</p>
        </Paper>
        {this.notifications.map(notification => (
          <div>
            <Grid
              justify="flex-start"
              container
              direction="row"
              alignItems="center"
            >
              <Button textAlign="left" className="notifi-button-item">
                <div className="item">
                  <Link underline="none">
                    <FontAwesomeIcon icon={faBell} style={{ fontSize: 20 }} />
                    <span className="textNotifi">{notification}</span>
                  </Link>
                </div>
              </Button>
            </Grid>
            <Divider />
          </div>
        ))}
      </div>
    );
  }
}

export default Notification;
