import React, { Component } from "react";
import {
  Divider,
  Grid,
  CardMedia,
  Button,
  Select,
  MenuItem
} from "@material-ui/core";
import "./Details.css";

class DetailsProduct extends Component {
  state = {
    imageSelect: "detail-black.jpg",
    color: "black",
    size: 40
  };
  render() {
    return (
      <div>
        <Grid
          style={{ justifyContent: "space-between" }}
          container
          direction="row"
        >
          <div class="image-product-display">
            <span className="product-name">Birkenstock Milano</span>
            <CardMedia
              className="image-product"
              image={require("../images/" + this.state.imageSelect)}
            />
            <Grid container direction="row">
              <Button
                onClick={() => {
                  this.setState({ imageSelect: "detail-black.jpg" });
                }}
              >
                <CardMedia
                  className="image-button"
                  image={require("../images/detail-black.jpg")}
                />
              </Button>
              <Button
                onClick={() => {
                  this.setState({ imageSelect: "detail-white.jpg" });
                }}
              >
                <CardMedia
                  className="image-button"
                  image={require("../images/detail-white.jpg")}
                />
              </Button>
              <Button
                onClick={() => {
                  this.setState({ imageSelect: "detail-white-2.jpg" });
                }}
              >
                <CardMedia
                  className="image-button"
                  image={require("../images/detail-white-2.jpg")}
                />
              </Button>
            </Grid>
          </div>
          <div className="form-action">
            <div className="price-container">
              <span className="price">$49.99</span>
            </div>
            <div className="select-container">
              <div className="select-margin">
                <span className="span-text">Colors:</span>
                <br />
                <Select
                  className="select"
                  style={{ width: 200 }}
                  value={this.state.color}
                  onChange={this.handleChangeColor.bind(this)}
                >
                  <MenuItem value={"black"}>Black</MenuItem>
                  <MenuItem value={"white"}>White</MenuItem>
                  <MenuItem value={"yellow"}>Yellow</MenuItem>
                </Select>
                <br />
              </div>
              <div className="select-margin">
                <span className="span-text">Size:</span>
                <br />
                <Select
                  className="select"
                  style={{ width: 200 }}
                  value={this.state.size}
                  onChange={this.handleChangeSize.bind(this)}
                >
                  <MenuItem value={40}>40</MenuItem>
                  <MenuItem value={50}>50</MenuItem>
                  <MenuItem value={60}>60</MenuItem>
                </Select>
              </div>
              <br />
              <Button
                style={{
                  color: "white",
                  backgroundColor: "#ae4343",
                  margin: 50,
                  height: 50,
                  width: 200,
                  fontWeight: "bold"
                }}
                className="button-add"
              >
                Add to Shopping Bag
              </Button>
            </div>
          </div>
        </Grid>
        <Divider style={{ height: 1, color: "black" }} />
        <h2 className="title-information">Product Information:</h2>
        <h2 className="product-name">Birkenstock</h2>
        <div className="information-product"></div>
        <h2 className="title-information">Comments:</h2>
        <div>
          <Grid>
            {}
          </Grid>
        </div>
      </div>
    );
  }
  handleChangeColor(event) {
    this.setState({ color: event.target.value });
  }
  handleChangeSize(event) {
    this.setState({ size: event.target.value });
  }
}

export default DetailsProduct;
