import React, { Component } from "react";
import {
  Button,
  AppBar,
  Toolbar,
  Input,
  CardMedia,
  IconButton,
  Grid,
  Menu,
  MenuItem,
  Fade
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faShoppingBag,
  faUserAlt,
  faBell
} from "@fortawesome/free-solid-svg-icons";

class Header extends Component {
  state = {
    textInput: "",
    isOpen: true
  };

  handleClick() {
    this.setState({ isOpen: !this.state.isOpen });
  }
  handleClose() {
    this.setState({ isOpen: false });
  }

  render() {
    return (
      <div>
        <AppBar position="static" color="primary">
          <Toolbar
            style={{
              alignContent: "center",
              alignItems: "center",
              justifyContent: "space-between"
            }}
          >
            <Toolbar>
              <CardMedia
                style={{ width: 70, height: 30, borderRadius: 10 }}
                image="https://storage.googleapis.com/bit-docs/bit-logo%402x.png"
              />
              <Button onClick={() => {}} style={{ color: "white" }}>
                Home
              </Button>
              <Button style={{ color: "white" }}>Men</Button>
              <Button style={{ color: "white" }}>Women</Button>
            </Toolbar>
            <Input
              onChange={this.inputHandle}
              placeholder="Search..."
              disableUnderline={true}
              style={{
                height: 40,
                width: 500,
                color: "gray",
                background: "white",
                paddingRight: 5,
                paddingLeft: 5,
                borderColor: "black",
                borderWidth: 2,
                borderRadius: 15,
                padding: "0 10px",
                border: 2,
                boxShadow: props =>
                  props.color === "red"
                    ? "0 3px 5px 2px rgba(255, 105, 135, .3)"
                    : "0 3px 5px 2px rgba(33, 203, 243, .3)"
              }}
            />
            <Grid>
              <IconButton>
                <FontAwesomeIcon
                  style={{ color: "white" }}
                  icon={faUserAlt}
                  onClick={() => {}}
                />
              </IconButton>
              <Menu open={this.state.isOpen} TransitionComponent={Fade}>
                <MenuItem onClick={this.handleClose}>Profile</MenuItem>
                <MenuItem onClick={this.handleClose}>My account</MenuItem>
                <MenuItem onClick={this.handleClose}>Logout</MenuItem>
              </Menu>
              <IconButton>
                <FontAwesomeIcon style={{ color: "white" }} icon={faBell} />
              </IconButton>
              <IconButton>
                <FontAwesomeIcon
                  style={{ color: "white" }}
                  icon={faShoppingBag}
                />
              </IconButton>
            </Grid>
          </Toolbar>
        </AppBar>
      </div>
    );
  }
}

export default Header;
