import React, { Component } from "react";
import { Avatar, Card, Grid, Input } from "@material-ui/core";

import "./MyAccount.css";

class MyAccount extends Component {
  render() {
    return (
      <div style={{ minHeight: 600 }}>
        <Card
          style={{
            padding: 20,
            marginTop: 20,
            marginLeft: 30,
            marginRight: 30,
            marginBottom: 10
          }}
        >
          <span style={{ fontSize: 17 }}>Thông tin tài khoản</span>
        </Card>
        <Card
          style={{
            marginLeft: 30,
            marginRight: 30,
            padding: 10
          }}
        >
          <Avatar
            className="avatar-style"
            style={{ width: 200, height: 200 }}
            src={require("../images/avatar.jpg")}
          />
          <Grid item direction="row">
            <span style={{ fontSize: 16, width: 300 }}>ID:</span>
            <span style={{ fontSize: 16 }}>23463</span>
          </Grid>
          <Grid item direction="row">
            <span style={{ fontSize: 16 }}>Họ tên: </span>
            <Input style={{ fontSize: 16 }} value="Nguyễn Thế An" />
          </Grid>
          <Grid item direction="row">
            <span style={{ fontSize: 16 }}>Số điện thoại: </span>
            <Input style={{ fontSize: 16 }} value="0399873737" />
          </Grid>
          <Grid item direction="row">
            <span style={{ fontSize: 16 }}>Email: </span>
            <Input style={{ fontSize: 16 }} value="Nguyễn Thế An" />
          </Grid>
        </Card>
      </div>
    );
  }
}

export default MyAccount;
