import React, { Component } from "react";
import {
  Grid,
  Card,
  GridList,
  Select,
  MenuItem,
  Radio,
  FormControlLabel,
  Button,
  GridListTile
} from "@material-ui/core";

import "./Men.css";
import Product from "../components/Product";
import SelectedButton from "../components/SelectedButton";
import axios from "axios";
import { getCategories } from "../api/Helper";
const womenShoesUrl = "https://webttcn.herokuapp.com/v1/shoes?kind=women";
const categoriesUrl = "https://webttcn.herokuapp.com/v1/categories";

class Woman extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sortSelect: "popular",
      liked: false,
      addToCart: false,
      shoes: [],
      fillters: [],
      categories: []
    };
  }

  componentDidMount() {
    axios.get(womenShoesUrl).then(res => {
      this.setState({ shoes: res.data });
      console.log(this.state.shoes);
    });
    axios
      .get(categoriesUrl)
      .then(res => {
        this.setState({ categories: res.data });
      })
      .catch(error => console.log(error));
  }

  render() {
    return (
      <div className="men-container">
        <Grid container className="center">
          <Grid item xs={2}>
            <p style={{ fontWeight: "bold", fontSize: 25 }}>Giày nữ</p>
            <Card style={{ padding: 26 }}>Bộ lọc kết quả</Card>
            <Card style={{ marginTop: 30 }}>
              <Card style={{ padding: 10 }}>Theo thương hiệu</Card>
              <Grid container className="center-select">
                <RadioGroup>
                  {this.state.categories.map(categori => (
                    <div>
                      <FormControlLabel
                        control={<Radio value="popular" />}
                        label={categori.name}
                      />
                      <br />
                    </div>
                  ))}
                </RadioGroup>
              </Grid>
            </Card>
            <Card style={{ marginTop: 30 }}>
              <Card style={{ padding: 10 }}>Theo giá</Card>
            </Card>
          </Grid>
          <Grid item xs={9} style={{ marginLeft: 10 }}>
            <Card style={{ padding: 20 }}>
              <Grid container style={{ width: 300, fontSize: 18 }}>
                Lựa chọn của bạn:
                <Grid container direction="row">
                  {["Màu đỏ", "Nhỏ hơn 100.000"].map(item => (
                    <SelectedButton
                      itemTitle={item}
                      onClick={() => {
                        console.log("1");
                      }}
                    />
                  ))}
                </Grid>
              </Grid>
              <Grid container className="header-sort">
                <Grid item>
                  Sắp xếp theo:{" "}
                  <Select
                    value={this.state.sortSelect}
                    style={{ marginLeft: 10, width: 160, fontSize: 15 }}
                    onChange={this.handleOnChangeSelectSort.bind(this)}
                  >
                    <MenuItem value="newest">Mới nhất</MenuItem>
                    <MenuItem value="popular">Phổ biến</MenuItem>
                    <MenuItem value="high-to-low">Giá từ cao đến thấp</MenuItem>
                    <MenuItem value="low-to-high">Giá từ thấp đến cao</MenuItem>
                  </Select>
                </Grid>
              </Grid>
            </Card>
            <GridList cellHeight={300} spacing={20} cols={3}>
              {this.state.shoes.map(shoe => (
                <GridListTile>
                  <Product
                    likeTitle="Lượt thích"
                    image={
                      "https://webttcn.herokuapp.com/uploads/1574585783033-shoe_1.png"
                    }
                    name={shoe.name}
                    price={shoe.price + " VNĐ"}
                  />
                </GridListTile>
              ))}
            </GridList>
            <div style={{ padding: 10, marginTop: 10 }}>
              <Button
                style={{ textTransform: "none", fontSize: 16, color: "blue" }}
                onClick={this.handleShowMore.bind(this)}
              >
                Xem thêm ...
              </Button>
            </div>
          </Grid>
        </Grid>
      </div>
    );
  }
  handleShowMore() {}
  handleOnChangeSelectSort(event) {
    this.setState({ sortSelect: event.target.value });
  }
}

export default Woman;
