import React, { Component } from "react";
import {
  Card,
  Grid,
  Input,
  Radio,
  Button,
  IconButton,
  CardMedia
} from "@material-ui/core";
import axios from "axios";

import "./MyAccount.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCamera } from "@fortawesome/free-solid-svg-icons";

const updateUrl = "https://webttcn.herokuapp.com/v1/users/1";
const getByIdUrl = "https://webttcn.herokuapp.com/v1/users/";

class MyAccount extends Component {
  constructor(props) {
    super(props);
    this.state = {
      avatarPath: "",
      idUser: "23463444",
      fullName: "Nguyễn Thế An",
      phoneNumber: "0399873737",
      email: "theannguyen98@gmail.com",
      sex: "Nam",
      oldPassword: "",
      newPassword: "",
      confirmNewPassword: ""
    };
    this.setupInformation();
  }

  componentDidMount() {}

  render() {
    return (
      <div style={{ minHeight: 600 }}>
        <Card
          style={{
            padding: 20,
            marginTop: 20,
            marginLeft: 50,
            marginRight: 50,
            marginBottom: 10
          }}
        >
          <span style={{ fontSize: 17, fontWeight: "bold" }}>
            Thông tin tài khoản
          </span>
        </Card>
        <Card
          style={{
            marginLeft: 50,
            marginRight: 50,
            padding: 10,
            marginBottom: 20
          }}
        >
          <CardMedia
            image={require("../images/avatar.jpg")}
            className="avatar-style"
          >
            <Button
              className="center-button-add-image"
              style={{ color: "white", marginTop: 70 }}
            >
              <FontAwesomeIcon icon={faCamera}>
                <input type="file" style={{ display: "none" }} />
              </FontAwesomeIcon>
            </Button>
          </CardMedia>
          <Grid
            spacing={3}
            container
            direction="column"
            style={{ marginTop: 20 }}
          >
            <Grid item>
              <span className="label">ID:</span>
              <Input className="text-field" value={this.state.idUser} />
            </Grid>
            <Grid item>
              <span className="label">Họ tên: </span>
              <Input
                className="text-field"
                value={this.state.fullName}
                onChange={event =>
                  this.setState({ fullName: event.target.value })
                }
              />
            </Grid>
            <Grid item>
              <span className="label">Số điện thoại: </span>
              <Input
                className="text-field"
                value={this.state.phoneNumber}
                onChange={event =>
                  this.setState({ phoneNumber: event.target.value })
                }
              />
            </Grid>
            <Grid item>
              <span className="label">Email: </span>
              <Input
                className="text-field"
                value={this.state.email}
                onChange={event => this.setState({ email: event.target.value })}
              />
            </Grid>
            <Grid item>
              <span className="label">Giới tính : </span>
              <Radio
                value="Nam"
                checked={this.state.sex === "Nam"}
                onChange={event =>
                  this.setState({
                    sex: event.target.value
                  })
                }
              />{" "}
              <span className="label" style={{ width: 65 }}>
                Nam
              </span>
              <Radio
                value="Nu"
                checked={this.state.sex === "Nu"}
                onChange={event =>
                  this.setState({
                    sex: event.target.value
                  })
                }
              />{" "}
              <span className="label" tyle={{ width: 65 }}>
                Nữ
              </span>
            </Grid>
            <Grid item>
              <span
                className="label"
                style={{ width: 450, fontWeight: "bold", fontSize: 17 }}
              >
                {" "}
                Thay đổi mật khẩu
              </span>
            </Grid>
            <Grid item>
              <span className="label">Mật khẩu cũ: </span>
              <Input
                className="text-field"
                value={this.state.oldPassword}
                onChange={event =>
                  this.setState({ oldPassword: event.target.value })
                }
                placeholder="Nhập mật khẩu cũ"
              />
            </Grid>
            <Grid item>
              <span className="label">Mật khẩu mới: </span>
              <Input
                type="password"
                className="text-field"
                value={this.state.newPassword}
                onChange={event =>
                  this.setState({ newPassword: event.target.value })
                }
                placeholder="Nhập mật khẩu mới"
              />
            </Grid>
            <Grid item>
              <span className="label">Nhập lại: </span>
              <Input
                type="password"
                className="text-field"
                value={this.state.confirmNewPassword}
                onChange={event =>
                  this.setState({ confirmNewPassword: event.target.value })
                }
                placeholder="Nhập lại mật khẩu mới"
              />
            </Grid>
            <Button
              style={{
                textTransform: "none",
                backgroundColor: "yellow",
                marginLeft: 500,
                marginRight: 500,
                marginTop: 10,
                marginBottom: 20
              }}
              onClick={this.handleUpdate.bind(this)}
            >
              Cập nhật
            </Button>
          </Grid>
        </Card>
      </div>
    );
  }

  setupInformation() {
    const idUser = localStorage.getItem("id_user", "none");
    if (idUser === "none") {
      return;
    }
    console.log(idUser);
    axios.get(getByIdUrl, idUser).then(res => {
      console.log(res.data);
    });
  }
  handleUpdate() {
    var isNotEmpty =
      this.state.email !== "" &&
      this.state.password !== "" &&
      this.state.firstName !== "" &&
      this.state.lastName !== "" &&
      this.state.phone !== "";
    if (isNotEmpty) {
      var user = {
        first_name: this.state.firstName,
        last_name: this.state.lastName,
        phone: this.state.phone,
        email: this.state.email,
        password: this.state.password
      };
      axios
        .post(updateUrl, user)
        .then(res => {
          console.log(res.data);
        })
        .catch(err => {
          alert("Cập nhật thông tin thất bại!!");
        });
    } else {
      alert("Enter your email or password");
    }
  }
}

export default MyAccount;
