import React, { Component } from "react";
import {
  GridList,
  Grid,
  Button,
  Card,
  Paper,
  Typography,
  IconButton
} from "@material-ui/core";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faMinus } from "@fortawesome/free-solid-svg-icons";
import "./Cart.css";

class Cart extends Component {
  state = {
    count: 1,
    price: 36000
  };
  render() {
    return (
      <div className="cart-container">
        <span className="cart-title">Cart</span>
        <GridList style={{ marginRight: 10, marginLeft: 70 }} cols={1}>
          {["white", "yellow", "blue", "red"].map(color => (
            <Card className="cardStyle">
              <Grid container direction="row" className="gridContainer">
                <div>
                  <Grid container direction="row">
                    <Link to="/details">
                      <img
                        src={require("../images/detail-white-2.jpg")}
                        alt="Cart"
                        className="image-product-cart"
                      />
                    </Link>
                    <Grid style={{justifyContent}} >
                      <span class="title-product-cart">
                        Giày Sneaker Nam Thời Trang - Pettino GV03 - Đen - 39
                      </span>
                      <br />
                      <span class="title-product-cart">Chỉ còn 5 sản phẩm</span>
                      <br />
                      <span class="title-product-cart">
                        Cung cấp bởi Pettino
                      </span>
                      <br />
                      <Button style={{ color: "blue", fontSize: 12 }}>
                        Delete
                      </Button>
                    </Grid>
                  </Grid>
                </div>

                <h4>{this.state.price} VND</h4>

                <Grid className="count" direction="column">
                  <IconButton
                    onClick={() => {
                      if (this.state.count > 1) {
                        this.setState({
                          count: this.state.count - 1,
                          price: this.state.price - 36000
                        });
                      }
                    }}
                  >
                    <FontAwesomeIcon icon={faMinus} className="iconStyle" />
                  </IconButton>
                  <h4>{this.state.count}</h4>
                  <IconButton
                    onClick={() => {
                      if (this.state.count < 100) {
                        this.setState({
                          count: this.state.count + 1,
                          price: this.state.price + 36000
                        });
                      }
                    }}
                  >
                    <FontAwesomeIcon icon={faPlus} className="iconStyle" />
                  </IconButton>
                </Grid>
              </Grid>
            </Card>
          ))}
        </GridList>
      </div>
    );
  }
}

export default Cart;

