import React, { Component } from "react";
import {
  GridList,
  Grid,
  Container,
  Button,
  Card,
  Paper,
  Typography,
  IconButton
} from "@material-ui/core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus, faMinus } from "@fortawesome/free-solid-svg-icons";
import "./Cart.css";

class Cart extends Component {
  state = {
    count: 0
  };
  render() {
    return (
      <Container>
        <h2>Cart</h2>
        <GridList style={{ marginRight: 100, marginLeft: 100 }} cols={1}>
          {["white", "yellow", "blue", "red"].map(color => (
            <Card>
              <Grid
                container
                direction="row"
                style={{
                  alignContent: "center",
                  alignItems: "center",
                  justifyContent: "space-between"
                }}
              >
                <img
                  src="https://salt.tikicdn.com/cache/175x175/ts/product/9c/ad/e4/1efb61539a6adcf2da14bd70b21ddf58.jpg"
                  alt="Cart"
                />
                <Paper style={{ width: 300, height: 100 }}>
                  <Typography variant="h5" component="h3">
                    Cà phê Sôcôla
                  </Typography>
                  <Typography component="p">
                    Paper can be used to build surface or other elements for
                    your application.
                  </Typography>
                  <Button
                    className="deleteButton"
                  >
                    Delete
                  </Button>
                </Paper>
                <h4>36.000đ</h4>

                <Grid className="count" container direction="row">
                  <IconButton>
                    <FontAwesomeIcon icon={faPlus} className="iconStyle" />
                  </IconButton>
                  <h4>{this.state.count}</h4>
                  <IconButton>
                    <FontAwesomeIcon icon={faMinus} className="iconStyle" />
                  </IconButton>
                </Grid>
              </Grid>
            </Card>
          ))}
        </GridList>
      </Container>
    );
  }
}

export default Cart;

