import React, { Component } from "react";
import {
  Grid,
  Card,
  Radio,
  FormControlLabel,
  RadioGroup
} from "@material-ui/core";
import InputRangeCustom from "../components/InputRangeCustom";
import axios from "axios";

const categoriesUrl = "https://webttcn.herokuapp.com/v1/categories";

export default class FillterSelect extends Component {
  constructor(props) {
    super(props);
    this.state = {
      categoryID: "",
      categoryName: "",
      categories: [],
      price: { min: 0, max: 100 },
      priceValue: 0,
      fillter: {}
    };
  }

  componentDidMount() {
    axios
      .get(categoriesUrl)
      .then(res => {
        this.setState({
          categories: res.data,
          categoryID: res.data[0]._id,
          fillter: {
            price: this.state.price,
            category: {
              _id: res.data[0]._id,
              name: res.data[0].name
            }
          }
        });
      })
      .catch(error => console.log(error));
  }
  render() {
    return (
      <Grid item xs={2}>
        <p style={{ fontWeight: "bold", fontSize: 25 }}>Giày nữ</p>
        <Card style={{ padding: 10 }}>Bộ lọc kết quả</Card>
        <Card style={{ marginTop: 30 }}>
          <Card style={{ padding: 10 }}>Theo thương hiệu</Card>
          <Grid container className="center-select">
            <RadioGroup
              value={this.state.categoryID}
              onChange={this.handleChangeCategory.bind(this)}
            >
              {this.state.categories.map(category => (
                <div>
                  <Grid>
                    <FormControlLabel
                      style={{ marginLeft: 20 }}
                      control={<Radio value={category._id} />}
                      label={category.name}
                    />
                  </Grid>
                  <br />
                </div>
              ))}
            </RadioGroup>
          </Grid>
        </Card>
        <Card style={{ marginTop: 30, padding: 30 }}>
          <Card style={{ padding: 10, marginBottom: 30 }}>
            Theo giá (Ngàn đồng)
          </Card>
          <InputRangeCustom
            onChangePrice={price => {
              this.setState({
                price: price,
                fillter: {
                  price: price,
                  category: this.state.category
                }
              });
              this.handleFillterOnChange();
            }}
          />
        </Card>
      </Grid>
    );
  }
  handleFillterOnChange() {
    this.props.onFillterChange(this.state.fillter);
  }
  handleChangeCategory(event) {
    this.setState({
      category: event.target.value,
      fillter: {
        price: this.state.price,
        category: {
          _id: event.target.value
        }
      }
    });
    console.log(event.target.value);
    this.props.onFillterChange(this.state.fillter);
  }
}
