import React, { Component } from "react";
import { Grid, Button, Divider, TextField, Card } from "@material-ui/core";
import CartProduct from "../components/CartProduct";

import axios from "axios";
import "./Cart.css";

const getShoeByID = "https://webttcn.herokuapp.com/v1/shoes";
const billsUrl = "https://webttcn.herokuapp.com/v1/bills";

class Cart extends Component {
  state = {
    priceAllProducts: 0,
    shoes: [],
    listOfShoes: [],
    amounts: {},
    shoePrices: {},
    address: ""
  };

  componentDidMount() {
    var idGets = JSON.parse(localStorage.getItem("ids"));
    if (idGets === "none") {
      idGets = [];
    } else {
      idGets.map(id => {
        axios.get(`${getShoeByID}/${id}`).then(res => {
          if (res.data != null) {
            this.setState({
              shoePrices: (this.state.shoePrices[res.data._id] =
                res.data.price),
              // amount: (this.state.amounts[res.data._id] = 1),
              shoes: [...this.state.shoes, res.data],
              priceAllProducts:
                this.state.priceAllProducts + res.data.price
                  ? 0
                  : res.data.price,
              listOfShoes: [
                ...this.state.listOfShoes,
                {
                  _id: res.data._id,
                  amount: this.state.amounts[res.data._id]
                    ? (this.state.amounts[res.data._id] = 1)
                    : (this.state.amounts[res.data._id] = this.state.amounts[
                        res.data._id
                      ])
                }
              ]
            });
          }
          console.log(res.data);
        });
      });
    }
  }

  render() {
    return (
      <div className="cart-container">
        <span className="cart-title">
          Giỏ hàng ({this.state.listOfShoes.length} Sản phẩm):
        </span>
        <div style={{ marginLeft: 30, marginRight: 30 }}>
          <Grid container spacing={2}>
            {this.state.listOfShoes.length === 0 ? (
              <Grid item xs={9}>
                <Card style={{ paddingTop: 100, paddingBottom: 100 }}>
                  Giỏ hàng rỗng
                </Card>
              </Grid>
            ) : (
              <Grid item xs={9}>
                {this.state.shoes.map(shoe => (
                  <CartProduct
                    handleSubRecieve={this.handleSub(shoe._id)}
                    handlePlusReceive={this.handlePlus(shoe._id)}
                    handleDeleteReceive={this.handleDelete(shoe._id)}
                    count={1}
                    name={shoe.name}
                    price={this.priceToString(shoe.price)}
                    category={shoe.category.name}
                    imageUrl={"https://webttcn.herokuapp.com/" + shoe.images[0]}
                  />
                ))}
              </Grid>
            )}
            <Grid
              style={{
                backgroundColor: "white",
                marginTop: 8,
                height: 270,
                borderRadius: 8
              }}
              item
              xs={3}
            >
              <Grid>
                <div
                  style={{
                    marginBottom: 20,
                    marginTop: 20
                  }}
                >
                  <div style={{ height: 50 }}>
                    <span style={{ fontSize: 15, color: "black" }}>
                      Tạm tính:{" "}
                    </span>
                    <span style={{ fontSize: 18, color: "red" }}>
                      {this.priceToString(this.state.priceAllProducts)}đ
                    </span>
                  </div>
                  <Divider />
                  <div style={{ height: 50, marginTop: 30 }}>
                    <span style={{ fontSize: 15, color: "black" }}>
                      Thành tiền:{" "}
                    </span>
                    <span style={{ fontSize: 18, color: "red" }}>
                      {this.priceToString(this.state.priceAllProducts)}đ
                    </span>
                    <br />
                    <span style={{ fontSize: 13 }}>
                      (Đã bao gồm VAT nếu có)
                    </span>
                  </div>
                  <div className="">
                    <div style={{ marginTop: 20 }}>
                      <span style={{ paddingRight: 5, fontSize: 15 }}>
                        Địa chỉ nhận hàng:{" "}
                      </span>
                    </div>
                    <TextField
                      style={{
                        width: 250,
                        marginTop: 20,
                        paddingLeft: 10
                      }}
                      value={this.state.address}
                      onChange={event =>
                        this.setState({ address: event.target.value })
                      }
                      placeholder="Nhập địa chỉ..."
                    />
                  </div>
                </div>
                <div
                  style={{
                    backgroundColor: "white",
                    marginTop: 90
                  }}
                >
                  <Button
                    className="button-order-style"
                    style={{
                      color: "white",
                      backgroundColor: "red",
                      fontWeight: "bold",
                      textTransform: "none",
                      fontSize: 17
                    }}
                    onClick={this.handleBills.bind(this)}
                  >
                    Thanh toán
                  </Button>
                </div>
              </Grid>
            </Grid>
          </Grid>
        </div>
      </div>
    );
  }

  handleBills() {
    const tokenUser = localStorage.getItem("token_user", "none");
    if (tokenUser == null) {
      window.location.replace("/login-or-register");
    }
    var bill = {
      userId: localStorage.getItem("id_user"),
      address: this.state.address,
      money: this.state.priceAllProducts,
      listOfShoes: this.state.listOfShoes
    };
    axios
      .post(billsUrl, bill, {
        headers: { Authorization: "Bearer " + tokenUser }
      })
      .then(res => {
        console.log(res.data);
      })
      .catch(err => {
        alert("Thanh toán thất bại!");
      });
  }

  handlePlus(shoeID) {
    this.setState({
      amounts: (this.state.amounts[shoeID] += 1),
      priceAllProducts:
        this.state.priceAllProducts + this.state.shoePrices[shoeID]
    });
  }

  handleSub(shoeID) {
    // this.setState({
    //   amounts: (this.state.amounts[shoeID] -= 1),
    //   priceAllProducts:
    //     this.state.priceAllProducts - this.state.shoePrices[shoeID]
    // });
  }

  priceToString(price) {
    return price.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1.");
  }

  handleDelete(shoeID) {
    var idGets = JSON.parse(localStorage.getItem("ids"));
    if (idGets === null) {
      idGets = [];
    } else {
      Array.prototype.remove = function() {
        var what,
          a = arguments,
          L = a.length,
          ax;
        while (L && this.length) {
          what = a[--L];
          while ((ax = this.indexOf(what)) !== -1) {
            this.splice(ax, 1);
          }
        }
        return this;
      };
      idGets.remove(shoeID);
      var idsJson = JSON.stringify(idGets);
      localStorage.setItem("ids", idsJson);
    }
  }
}

export default Cart;
